import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      cui: '',
      data:[]
    };
    this.submit = this.submit.bind(this);
  }

  handleChange = event => {
    this.setState({ cui: event.target.value });
  };

  submit(){
    var cui = this.state.cui
    const url = `https://business-api.nano-soft.ro/v0.1/business/${cui}`
    fetch(url)
    .then(res => {
      console.log(res.json())
    }).then(res => {
      console.log(res.results[0])
    })
    .catch(error => {         
      console.log(error)
    });
  }
  render(){
    console.log(this.state.cui)

  return (
        <div style={{margin:40}}>
          <form onSubmit={this.submit}>
            <label>
              CUI:
              <input type="text" name='cui' value={this.state.cui} maxLength={9} onChange={this.handleChange}/>
            </label>
            <input type="submit" value="Submit"  />
          </form>
        </div>
  );
}
}

export default App;
